	@Override
	public void configure() throws Exception {
		....
		CxfEndpoint endpoint = new CxfEndpoint();
		endpoint.setAddress("/PingPong");
		endpoint.setBus(bus);
		endpoint.setServiceClass(PingPongService.class);

		// @formatter:off
		from(endpoint)	
               ....
